import { Component } from "react"; //imports Component Class From react module
import axios from "axios"; //imports axios Class From react module

import "./index.css"; //imports CSS
import Header from "../Header"; //imports Header
import DictionaryItem from "../DictionaryItem"; //imports DictionaryItem
import DetailedView from "../DetailedView"; //imports DetailedView

class Home extends Component {
  state = { wordsList: [] };

  componentDidMount() {
    this.getData();
  }

  getData = async () => {
    const app_id = "b9f7e093";
    const app_key = "89a5d169061121d96efa5b91cf6ef789";
    const wordId = "ace";
    const fields = "pronunciations";
    const strictMatch = "false";
    const baseURL = "https://od-api.oxforddictionaries.com/api/v2";

    const options = {
      host: "od-api.oxforddictionaries.com",
      port: "443",
      path:
        "/api/v2/entries/en-gb/" +
        wordId +
        "?fields=" +
        fields +
        "&strictMatch=" +
        strictMatch,
      method: "GET",
      headers: {
        app_id: app_id,
        app_key: app_key,
      },
    };
    axios.get(baseURL).then((response) => {
      this.setState({ wordsList: response });
    });
  };

  render() {
    return (
      <div className="bg-main-container">
        <Header />
        <div className="bg-wordslist-container">
          <p>Words List</p>
          <div>
            <ul>
              <DictionaryItem />
            </ul>
          </div>
        </div>
      </div>
    );
  }
}

export default Home;
