import { useContext } from "react";
import { UniversalState } from "../../Helper/Context";

const DetailedView = (props) => {
  const wordsList = useContext(UniversalState);
  const { match } = props;
  const { params } = match;
  const { id } = params;
  const filteredItem = wordsList.filter((each) => each.id === id);
  return (
    <div>
      <h1>{filteredItem.title}</h1>
      <p>description</p>
    </div>
  );
};

export default DetailedView;
