import { useState } from "react";
import { withRouter } from "react-router-dom";
import axios from "axios";
const CreateWord = (props) => {
  const [word, setWord] = useState("");
  const [Loading, setLoading] = useState(true);
  const wordChanged = (event) => {
    setWord(event.target.value);
  };
  const getData = async () => {
    const data = await axios.get(`http://localhost:4000/create/${word}`);
    console.log(data.data);
  };
  const goToHome = () => {
    const { history } = props;
    history.replace("/");
  };
  return (
    <div>
      <label htmlFor="word">New Word</label>
      <br />
      <input type="text" id="word" value={word} onChange={wordChanged} />
      <button onClick={goToHome}>Cancel</button>
      <button onClick={getData}>Add</button>
    </div>
  );
};

export default withRouter(CreateWord);
