import { useState } from "react";
import { withRouter } from "react-router-dom";
import axios from "axios";
import "./index.css";
const CreateWord = (props) => {
  const [word, setWord] = useState("");
  const [Loading, setLoading] = useState(true);

  const wordChanged = (event) => {
    setWord(event.target.value);
  };

  const getData = async () => {
    const data = await axios.get(`http://localhost:4000/create/${word}`);
    const { history } = props;
    history.replace("/");
  };
  const goToHome = () => {
    const { history } = props;
    history.replace("/");
  };
  return (
    <div className="addbutton-container">
      <label htmlFor="word">New Word</label>
      <input
        type="text"
        id="word"
        className="input"
        value={word}
        onChange={wordChanged}
      />
      <div className="buttons-container">
        <button className="button" onClick={goToHome}>
          Cancel
        </button>
        <button className="button" onClick={getData}>
          Add
        </button>
      </div>
    </div>
  );
};

export default withRouter(CreateWord);
