import { Component } from "react"; //imports Component Class From react module
import "./index.css";
import { UniversalState } from "../../Helper/Context";

class Header extends Component {
  mainHeader = () => {
    return (
      <>
        <div className="sub-container">
          <h1 className="header-title">Vocab</h1>
        </div>
        <div className="sub-container">
          <button type="button" className="search-button">
            <i className="fas fa-search"></i>
          </button>
        </div>
      </>
    );
  };

  searchButton = (search) => {
    return (
      <>
        <div className="sub-container search-box-container">
          <input
            type="text"
            className="search-input"
            value={search}
            placeholder="Search"
          />
        </div>
        <div className="sub-container">
          <button type="button" className="search-button">
            <i className="fas fa-times"></i>
          </button>
        </div>
      </>
    );
  };
  render() {
    <UniversalState.Consumer>
      {(value) => {
        const { wordsList } = value;
        return <p>{wordsList.title}</p>;
      }}
    </UniversalState.Consumer>;
  }
}
export default Header;
