import axios from "axios";
import "./index.css";
class CreateWord extends Component {
  state = { word: "", isLoading: false };
  wordChanged = (event) => {
    setWord(event.target.value);
  };

  getData = async () => {
    const data = await axios.get(`http://localhost:4000/create/${word}`);
    const { history } = props;
    history.replace("/");
  };
  goToHome = () => {
    const { history } = props;
    history.replace("/");
  };
  render() {
    return (
      <div className="addbutton-container">
        <label htmlFor="word">New Word</label>
        <input
          type="text"
          id="word"
          className="input"
          value={word}
          onChange={wordChanged}
        />
        <div className="buttons-container">
          <button className="button" onClick={goToHome}>
            Cancel
          </button>
          <button className="button" onClick={getData}>
            Add
          </button>
        </div>
      </div>
    );
  }
}

export default withRouter(CreateWord);
