import { Component } from "react"; //imports Component Class From react module
import axios from "axios"; //imports axios Class From react module

import "./index.css"; //imports CSS
import Header from "../Header"; //imports Header
import DictionaryItem from "../DictionaryItem"; //imports DictionaryItem
import DetailedView from "../DetailedView"; //imports DetailedView

class Home extends Component {
  state = {
    wordsList: [
      {
        title: "google",
        description:
          "search for information about (someone or something) on the internet using the search engine Google",
      },
      {
        title: "google",
        description:
          "search for information about (someone or something) on the internet using the search engine Google",
      },
    ],
  };

  componentDidMount() {
    this.getData();
  }

  getData = async () => {
    const dictionaryItem = await axios.get("http://localhost:4000/book");
    const data = dictionaryItem.data;
    this.setState((prev) => ({ wordsList: [...prev.wordsList, data] }));
  };

  render() {
    const { wordsList } = this.state;
    console.log(wordsList);
    return (
      <div className="bg-main-container">
        <Header />
        <div className="bg-wordslist-container">
          <p>Words List</p>
          <div>
            <ul>
              {wordsList.map((eachDictionaryItem, index) => (
                <DictionaryItem eachItem={eachDictionaryItem} key={index} />
              ))}
            </ul>
          </div>
        </div>
      </div>
    );
  }
}

export default Home;
