import { Component } from "react"; //imports Component Class From react module
import axios from "axios"; //imports axios Class From react module

import "./index.css"; //imports CSS
import Header from "../Header"; //imports Header
import DictionaryItem from "../DictionaryItem"; //imports DictionaryItem

class Home extends Component {
  state = {
    wordsList: [
      {
        id: "6bbb3551-98b8-4b03-b91a-761784d9be14",
        title: "good",
        description: "to be desired or approved of",
        lexicalCategory: "Adjective",
        detailedView: {
          etymologies: [
            "Old English gōd, of Germanic origin; related to Dutch goed and German gut",
          ],
          secondDefinition: "that which is morally right; righteousness",
          secondlexicalCategory: "Adjective",
          secondExamples: [{ text: "a mysterious balance of good and evil" }],
        },
      },
    ],
  };

  componentDidMount() {
    this.getData();
  }

  getData = async () => {
    const dictionaryItem = await axios.get("http://localhost:4000/google");
    const data = dictionaryItem.data;
    this.setState((prev) => ({ wordsList: [...prev.wordsList, data] }));
  };

  render() {
    const { wordsList } = this.state;
    console.log(wordsList);
    return (
      <div className="bg-main-container">
        <Header />
        <div className="bg-wordslist-container">
          <div className="wordslist-title-container">
            <p className="wordslist-title">Words List</p>
          </div>
          <div className="wordslist-inner-container">
            {wordsList.map((eachDictionaryItem) => (
              <DictionaryItem
                eachItem={eachDictionaryItem}
                key={eachDictionaryItem.id}
              />
            ))}
          </div>
        </div>
      </div>
    );
  }
}

export default Home;
