import { useState } from "react";
import axios from "axios";
import "./index.css";
const CreateWord = (props) => {
  const [word, setWord] = useState("");
  const [showErr, setshowErr] = useState(false);
  const [showLoader, setshowLoader] = useState(false);
  //const { getData, errorMsg } = props;

  const getDataFunction = async () => {
    setshowLoader(true);
    if (word !== "") {
      const data = await axios.get(`http://localhost:4000/create/${word}`);
      setshowErr(false);
      setshowLoader(false);
      setWord("");
    }
    if (word === "") {
      setshowErr(true);
    }
  };

  return (
    <div className="addbutton-container">
      <label htmlFor="word">New Word</label>
      <input
        type="text"
        id="word"
        className="input"
        onChange={(e) => setWord(e.target.value)}
      />
      {showErr && <p>Enter Valid input</p>}
      <div className="buttons-container">
        <button className="button" data-dismiss="modal">
          Cancel
        </button>
        <button className="button" onClick={getDataFunction}>
          Add
        </button>
      </div>
    </div>
  );
};

export default CreateWord;
