import { Component } from "react";
import { useHistory } from "react-router-dom";
import axios from "axios";
import "./index.css";
class CreateWord extends Component {
  state = { word: "", isLoading: false };

  wordChanged = (event) => {
    setWord(event.target.value);
  };

  getData = async () => {
    const data = await axios.get(
      `http://localhost:4000/create/${this.state.word}`
    );
    const history = useHistory();
    history.replace("/");
  };
  goToHome = () => {
    const history = useHistory();
    history.replace("/");
  };
  render() {
    return (
      <div className="addbutton-container">
        <label htmlFor="word">New Word</label>
        <input
          type="text"
          id="word"
          className="input"
          value={this.word}
          onChange={this.wordChanged}
        />
        <div className="buttons-container">
          <button className="button" onClick={this.goToHome}>
            Cancel
          </button>
          <button className="button" onClick={this.getData}>
            Add
          </button>
        </div>
      </div>
    );
  }
}

export default withRouter(CreateWord);
