import { Component } from "react";
import { Link } from "react";
import axios from "axios";

class DetailedView extends Component {
  state = { wordsList: [] };

  componentDidMount() {
    this.getData();
  }

  getData = async () => {
    const { id } = this.props.match.params;
    const response = await axios.get(
      `http://localhost:4000/word-details/${id}`
    );
    console.log(response.data);
    this.setState({ wordsList: response.data });
  };

  render() {
    const { wordsList } = this.state;

    return (
      <div>
        <div>
          <button onClick={this.goToHome}>
            <i className="fas fa-times"></i>
          </button>
        </div>

        <div>
          <h1>{wordsList.title}</h1>
          <p>Origin {wordsList.etymologies}</p>
          <p>{wordsList.description}</p>
          <ul>
            <li>{wordsList.examples}</li>
          </ul>
          <hr />
          <p>{wordsList.secondlexicalCategory}</p>
          <p>{wordsList.secondDefinition}</p>
          <ul>
            <li>{wordsList.secondExamples}</li>
            {wordsList.thirdExamples && <li> {wordsList.thirdExamples}</li>}
          </ul>
        </div>
      </div>
    );
  }
}

export default DetailedView;
